import React, { useState, useEffect } from 'react';
import './App.scss';
import axios from 'axios';
import Header from './components/Header.js';
import List from './components/List.js'

function App() {
  const [todo, setTodo] = useState({}); // Initialize todo as an empty object
  const [todos, setTodos] = useState([]);
  const [editId, setEditId] = useState("");

  useEffect(() => {
    getSubmitForm();
  }, []);

  const getSubmitForm = () => {
    axios.get('./api')
      .then((response) => {
        const data = response.data;
        setTodos(data); // Set todos as an array of objects received from the API
        console.log('Data received');
      })
      .catch(() => {
        alert('Error retrieving data');
      });
  }

  const handleChange = (event) => {
    const { name, value } = event.target;
    setTodo((prevTodo) => ({
      ...prevTodo,
      [name]: value
    }));
  };

  const submit = (event) => {
    event.preventDefault();

    if (editId !== "") {
      // Update an existing item in the state array
      const updatedTodos = todos.map((item, index) =>
        index === editId ? { ...item, ...todo } : item
      );
      setTodos(updatedTodos);
      setEditId("");
      setTodo({});
    } else {
      // Add a new item to the state array
      setTodos([
        ...todos,
        {
          Id: `${todo.name}-${Date.now()}`,
          ...todo,
        },
      ]);
      resetSubmitForm();
    }
  };

  const resetSubmitForm = () => {
    setTodo({
      name: '',
      email: '',
      resourceSiteCode: '',
      resourceSiteName: '',
      county: '',
      desiredEnergizationDate: '',
      totalNumberOfInverters: ''
    });
  };

  const handleEdit = (id) => {
    setEditId(id);
    const editTodo = todos.find((item) => item.Id === id);
    setTodo(editTodo);
  };

  const handleDelete = (id) => {
    const updatedTodos = todos.filter((item) => item.Id !== id);
    setTodos(updatedTodos);
  };

  return (
    <>
      <div className="header">
        <Header />
      </div>
      <div className="formContainer">
        <form className="material-form" onSubmit={submit}>
          {/* Input fields for form */}
          {/* ... */}
          <button className="material-form__button">Submit</button>
        </form>
      </div>
      <ul className="allTodos">
        <List
          list={todos}
          handleEdit={handleEdit}
          handleDelete={handleDelete}
        />
      </ul>
    </>
  );
}

export default App;
